function Flake(){var t=window.innerWidth,e=window.innerHeight;this.x=Math.floor(Math.random()*t),this.y=Math.floor(-900*Math.random()),this.radius=Math.floor(3*Math.random()+1),this.speed=Math.floor(6*Math.random()+3),this.update=function(e){this.y+=this.speed;var n=.01*(t/2-e);this.x+=t>=e?-n:n},this.draw=function(){this.y>=e&&(this.x=Math.floor(Math.random()*t),this.y=Math.floor(-100*Math.random()),this.z=Math.floor(3*Math.random()+1));var n=document.getElementById("canvas"),a=n.getContext("2d");a.beginPath(),a.arc(this.x,this.y,this.radius,0,2*Math.PI),a.fillStyle="#fff",a.fill()}}function createCanvas(){var t=document.createElement("canvas");t.addEventListener("mousemove",function(t){mouseX=t.clientX}),t.addEventListener("resize",function(t){resizeCanvas()}),context=t.getContext("2d"),t.id="canvas",context.canvas.width=window.innerWidth,context.canvas.height=window.innerHeight,document.body.appendChild(t)}function resizeCanvas(){context.canvas.width=window.innerWidth,context.canvas.height=window.innerHeight}function createFlakes(){for(var t=600,e=[],n=0;t>n;n++)e.push(new Flake);return e}function renderFlakes(){context.clearRect(0,0,canvas.width,canvas.height);for(var t=0;t<flakes.length;t++)flakes[t].update(mouseX),flakes[t].draw();setTimeout(renderFlakes,40)}window.onload=function(){mouseX=window.innerWidth/2,createCanvas(),flakes=createFlakes(),renderFlakes()};
